15ad5e564e149a3aa223b4c922404968153cfbbb.query:
query GetEntityOverview($id: ID!, $tenantId: ID!) {
  entity(id: $id, tenantId: $tenantId) {
    __typename
    ... on Entity {
      id
      tenantId
      kind
      displayName
      summary
      riskScore
      createdAt
      updatedAt
    }
    ... on ResolverError {
      code
      message
    }
  }
}

a29e16878156c4a362aa9f11cdbb2492f23f6cf5.query:
query ListEntities($tenantId: ID!, $first: Int!, $after: Cursor, $search: String) {
  entities(
    filter: { tenantId: $tenantId, search: $search }
    pagination: { first: $first, after: $after }
  ) {
    nodes {
      id
      displayName
      kind
      riskScore
      updatedAt
    }
    pageInfo {
      hasNextPage
      endCursor
      totalCount
    }
  }
}

8d73903aba2fbb001093e32bb599405d251e0450.query:
query GetEntityRelationships($entityId: ID!, $tenantId: ID!, $first: Int!, $after: Cursor) {
  entity(id: $entityId, tenantId: $tenantId) {
    __typename
    ... on Entity {
      id
      relationships(pagination: { first: $first, after: $after }) {
        edges {
          cursor
          node {
            id
            tenantId
            kind
            sourceId
            targetId
            confidence
            updatedAt
          }
        }
        pageInfo {
          hasNextPage
          endCursor
        }
      }
    }
    ... on ResolverError {
      code
      message
    }
  }
}

