  private handleToolsList(ws: WebSocket, message: MCPRequest): void {
    const response: MCPResponse = {
      jsonrpc: '2.0',
      id: message.id,
      result: {
        tools: this.tools,
      },
    };
    ws.send(JSON.stringify(response));
  }

